<div class="wrap"><h2><?php print MFCI_PUGIN_NAME ." ". MFCI_CURRENT_VERSION?></h2><form method="post" action="options.php">    <?php		settings_fields( 'mfci-settings-group' );	?>    <table class="form-table">        <tr valign="top">        <th scope="row">Image Size</th>        <td>                <?php		$wp_sizes = get_intermediate_image_sizes();					// Return false if empty			if( empty( $wp_sizes ) || !is_array( $wp_sizes ) )				return false;						echo '<select name="option_image_size">';			if(get_option('option_image_size') == '' ){					$selected = 'selected="selected"';			}			echo '<option ' .$selected. ' value="">default</option>';			$selected = '';			foreach( $wp_sizes as $wp_name){				if( get_option('option_image_size') == $wp_name){					$selected = 'selected="selected"';				}				echo '<option ' .$selected. ' value="' .$wp_name. '">' .$wp_name. '</option>';				$selected = '';			}        ?>        </tr>                <tr valign="top">        	<th scope="row">Add CSS (<a href="http://caniuse.com/#search=max-width">work in these browsers</a>)</th>        	<td>				<input type="checkbox" name="option_add_css" value="1"<?php checked( 1 == get_option( 'option_add_css' ) ); ?> />        		<label for="option_add_css">Add CSS to make full size content images responsive (max-width: 100%;)</label>        	</td>        </tr>        <tr>        	<th scope="row">Add class for content area (if left blank default is entry-content)</th>        	<td><input type="text" name="options_add_css_class" value="<?php echo  get_option('options_add_css_class'); ?>" /></td>		</tr>    </table>        <p class="submit">    	<input type="submit" class="button-primary" value="<?php _e('Save Changes') ?>" />    </p></form></div>